  getAllProd():Observable<Iprod[]>{
    return of(this.productArr)
  }

  getSingleProd(id : string):Observable<Iprod>{
    let findObj = this.productArr.find(f=>f.id === id) as Iprod
    return of(findObj)
  }

  addProd(obj : Iprod):Observable<number>{
    return of(this.productArr.unshift(obj))
  }

  updateProd(obj : Iprod): Observable<Iprod>{
    let getIndex = this.productArr.findIndex(f=>f.id === obj.id)
    this.productArr[getIndex] = obj
    return of(obj)
  }

  removeProd(id : string): Observable<string>{
    let getIndex = this.productArr.findIndex(f=>f.id === id)
    this.productArr.splice(getIndex, 1)
    return of(id)
  }
  nextProd(id :string):Observable<Iprod>{
   let single = this.productArr.find(f=>f.id !==id) as Iprod
    return of(single)
  }
